mapper.select:
select *
	from FavoriteRoom
	where personType = #{type,jdbcType=VARCHAR}
		and personId = #{id,jdbcType=VARCHAR}
		and roomBuilding = #{building,jdbcType=VARCHAR}
		and roomNumber = #{number,jdbcType=VARCHAR};

mapper.delete:
delete
	from FavoriteRoom
	where personType = #{type,jdbcType=VARCHAR}
		and personId = #{id,jdbcType=VARCHAR}
		and roomBuilding = #{building,jdbcType=VARCHAR}
		and roomNumber = #{number,jdbcType=VARCHAR};

delete
	from FavoriteRoom
	where personType = #{type,jdbcType=VARCHAR}
		and personId = #{id,jdbcType=VARCHAR};
